package com.leetcode.hard;

import java.text.SimpleDateFormat;
import java.util.Date;

/**
 * Implement wildcard pattern matching with support for '?' and '*'.
 * <p/>
 * '?' Matches any single character.
 * '*' Matches any sequence of characters (including the empty sequence).
 * <p/>
 * The matching should cover the entire input string (not partial).
 * <p/>
 * The function prototype should be:
 * bool isMatch(const char *s, const char *p)
 * <p/>
 * Some examples:
 * isMatch("aa","a") → false
 * isMatch("aa","aa") → true
 * isMatch("aaa","aa") → false
 * isMatch("aa", "*") → true
 * isMatch("aa", "a*") → true
 * isMatch("ab", "?*") → true
 * isMatch("aab", "c*a*b") → false
 * Subscribe to see which companies asked this question
 * <p/>
 * https://leetcode.com/problems/wildcard-matching/
 */
public class WildcardMatching {

    public static class Solution {

        public boolean isMatch(String s, String p) {
            boolean[][] cache = new boolean[s.length()][p.length()];
            for (int i = 0; i < s.length(); i++) {
                for (int j = 0; j < p.length(); j++) {
                    if (p.charAt(j) == '?')
                        if (j == 0 && j == 0)
                            cache[i][j] = true;
                        else
                            cache[i][j] = j != 0 && i != 0 && cache[i - 1][j - 1];
                    else if (p.charAt(j) == '*') {
                        if (j == 0)
                            cache[i][j] = true;
                        else {
                            boolean reslut = false;
                            for (int m = 0; m <= i; m++)
                                if (cache[m][j - 1])
                                    reslut = true;
                            cache[i][j] = reslut;
                        }
                    } else {
                        if (i == 0 || j == 0) {
                            if (i != 0 || j != 0)
                                cache[i][j] = false;
                            else
                                cache[i][j] = true;
                        } else
                            cache[i][j] = ((j == 0 && i == 0) || cache[i - 1][j - 1]) && s.charAt(i) == p.charAt(j);
                    }
                }
            }
            return cache[s.length() - 1][p.length() - 1];
        }
    }

    public static void main(String[] args) {
        System.out.println(new SimpleDateFormat("hh:mm:ss SSS").format(new Date()));
        System.out.println(new Solution().isMatch("aa", "a"));
        System.out.println(new Solution().isMatch("aa", "aa"));
        System.out.println(new Solution().isMatch("aaa", "aa"));
        System.out.println(new Solution().isMatch("aa", "*"));
        System.out.println(new Solution().isMatch("aa", "a*"));
        System.out.println(new Solution().isMatch("ab", "?*"));
        System.out.println(new Solution().isMatch("aab", "c*a*b"));
        System.out.println(new Solution().isMatch("aab", "a**a*b*"));
        //System.out.println(new Solution().isMatch("", "**"));
        //System.out.println(new Solution().isMatch("aaabbbaabaaaaababaabaaabbabbbbbbbbaabababbabbbaaaab", "a*******b"));
        //System.out.println(new Solution().isMatch("abbabaaabbabbaababbabbbbbabbbabbbabaaaaababababbbabababaabbababaabbbbbbaaaabababbbaabbbbaabbbbababababbaabbaababaabbbababababbbbaaabbbbbabaaaabbababbbbaababaabbababbbbbababbbabaaaaaaaabbbbbaabaaababaaaabb", "**aa*****ba*a*bb**aa*ab****a*aaaaaa***a*aaaa**bbabb*b*b**aaaaaaaaa*a********ba*bbb***a*ba*bb*bb**a*b*bb"));
        System.out.println(new SimpleDateFormat("hh:mm:ss SSS").format(new Date()));
    }

}
